mapper.select:
SELECT USER_NO,USER_ID, USER_NAME, DEPT_NAME, JOB_NAME FROM MEMBER JOIN DEPARTMENT USING (DEPT_CODE) JOIN JOB USING(JOB_CODE) WHERE STATUS = 'Y'
AND USER_NAME LIKE '%'||#{keyword}||'%'
AND USER_NAME LIKE '%'||#{keyword}||'%' AND DEPT_CODE = #{deptCode}
AND DEPT_CODE = #{deptCode}
ORDER BY JOB_CODE

mapper.update:
UPDATE MESSENGER 
	SET READ_STATUS = 'Y'
	WHERE MESSENGER_NO = #{msgNo}

mapper.select:
SELECT COUNT(*) FROM MESSENGER JOIN MEMBER ON (RECEIVER = USER_NO) WHERE RECV_DELETE = 'N' AND RECEIVER = #{userNo}
AND USER_NAME LIKE '%'||#{keyword}||'%'
AND MSG_TITLE LIKE '%'||#{keyword}||'%'

SELECT COUNT(*) FROM MESSENGER JOIN MEMBER ON (RECEIVER = USER_NO) WHERE SEND_DELETE = 'N' AND SENDER = #{userNo}
AND USER_NAME LIKE '%'||#{keyword}||'%'
AND MSG_TITLE LIKE '%'||#{keyword}||'%'
